local self = GInterface.CreateDefinition ("Database")self:AddMethod ("Connect")	:AddArgument ("string", "hostname")			:End ()	:AddArgument ("number", "port")				:End ()	:AddArgument ("string", "username")			:End ()	:AddArgument ("string", "password")			:End ()	:AddArgument ("string", "dbname")			:End ()	:AddArgument ("function", "callback")		:PushCallback ()			:AddArgument ("string", "error")				:SetCanBeNil (true)			:End ()		:End ()	:End ()	self:AddMethod ("Disconnect")	:AddArgument ("function", "callback")		:PushCallback ()			:AddArgument ("string", "error")				:SetCanBeNil (true)			:End ()		:End ()	:End ()self:AddMethod ("EscapeString")	:AddArgument ("string", "string")			:End ()	:SetReturnType ("string")self:AddMethod ("GetDatabaseListQuery")	:SetReturnType ("string")self:AddMethod ("GetTableListQuery")	:SetReturnType ("string")self:AddMethod ("IsConnected")	:SetReturnType ("boolean")self:AddMethod ("Query")	:AddArgument ("string", "query")			:End ()	:AddArgument ("function", "callback")		:PushCallback ()			:AddArgument ("string", "error")				:SetCanBeNil (true)			:End ()			:AddArgument ("anything", "result")				:SetCanBeNil (true)			:End ()		:End ()	:End ()