local self = {}GInterface.InterfaceProviderInfo = GInterface.MakeConstructor (self)function self:ctor (interface, name, provider)	self.Interface = interface	self.Name = name	self.Provider = provider	self.ProviderConstructor = GInterface.MakeConstructor (provider)		self.Checked = false	self.LastCreatedInterface = nil	self.Singleton = falseendfunction self:CheckProvider ()	self.Checked = true	local definition = GInterface.GetInterfaceDefinition (self:GetInterfaceName ())	if not definition then		ErrorNoHalt ("GInterface: Definition for " .. self:GetInterfaceName () .. " not found.\n")		return	end	local missing = definition:CheckProvider (self.Provider)	if #missing > 0 then		ErrorNoHalt ("GInterface: " .. self.Interface .. "::" .. self.Name .. " is missing " .. tostring (#missing) .. " methods.\n")		for _, v in ipairs (missing) do			ErrorNoHalt ("\t" .. self.Interface .. "::" .. self.Name .. "::" .. v .. "\n")		end	endendfunction self:CreateInterface (...)	if not self.Checked then		self:CheckProvider ()	end	if self:IsSingleton () and self.LastCreatedInterface then		return self.LastCreatedInterface	end	local interface = self.ProviderConstructor (...)	self.LastCreatedInterface = interface	return interfaceendfunction self:GetInterfaceName ()	return self.Interfaceendfunction self:GetName ()	return self.Nameendfunction self:IsSingleton ()	return self.Singletonendfunction self:SetSingleton (singleton)	self.Singleton = singletonend