local self = {}GInterface.Method = GInterface.MakeConstructor (self)function self:ctor (name)	self.Name = name		self.Arguments = {}	self.ReturnType = "nil"endfunction self:AddArgument (type, name)	local argument = GInterface.Argument (self, type, name)		self.Arguments [#self.Arguments + 1] = argument	return argumentendfunction self:GetArgumentCount ()	return #self.Argumentsendfunction self:GetReturnType ()	return self.ReturnTypeendfunction self:SetReturnType (type)	self.ReturnType = type	return selfend