local self, info = GInterface.CreateInterfaceProvider ("Servers", "Servers")info:SetPriority (0)info:SetSingleton (true)function self:ctor ()	self.CurrentServer = nil	self.Cache = {}		self.CurrentServer = self:GetServerByIP (GetConVarString ("hostip"), GetConVarNumber ("hostport"))	self.CurrentServer:SetCurrentServer (true)	self.CurrentServer:SetName (GetGlobalVar ("ServerName"))endfunction self:GetCurrentServer ()	return self.CurrentServerendfunction self:GetServerByIP (ip, port)	local server = self.Cache [ip .. ":" .. tostring (port)]	if not server then		server = GInterface.CreateInterface ("Server", nil, ip, port, nil)	end	return serverend