local self, info = GInterface.CreateInterfaceProvider ("Database", "SQLite")info:SetPriority (0)info:SetSingleton (true)function self:ctor ()endfunction self:Connect (server, port, username, password, callback)	callback = callback or function () end	callback (nil)endfunction self:Disconnect (callback)	callback = callback or function () end	callback (nil)endfunction self:EscapeString (string)	return sql.SQLStr (string)endfunction self:GetDatabaseListQuery ()	return ""endfunction self:GetTableListQuery (database)	return "SELECT * FROM sqlite_master WHERE type = \"table\""endfunction self:IsConnected ()	return trueendfunction self:Query (query, callback)	callback = callback or function () end	local result = sql.Query (query)	if result == false then		callback (sql.GetLastError (), nil)	else		callback (nil, result)	endend